import React, { useContext } from "react";
import { LoginContext } from "../LoginWindow/LoginContext";
import { useNavigate } from "react-router-dom";
import searchImage from "../assets/images/search.png";
function MainWindow() {
  const [context, setContext] = useContext(LoginContext);
  const navigate = useNavigate();

  function handleLogout() {
    setContext("");
    navigate("/");
  }

  return (
    <>
      {/* border border-primary */}
      <nav className="navbar navbar-dark bg-dark container ">
        <div className="container-fluid">
          <a className="navbar-brand">Olim Tech</a>
          <form className="d-flex  justify-content-center align-items-center">
            <div className={context && "user-name-top"}>{context}</div>
            <button
              className="btn btn-outline-success"
              type="submit"
              onClick={handleLogout}
            >
              Logout
            </button>
          </form>
        </div>
      </nav>
      <div className="container mt-5 rounded-3 p-0">
        <nav className="navbar navbar-expand-lg bg-light rounded-3 ">
          <div className="container-fluid">
            <form role="search">
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
            </form>
            <button
              className="btn btn-outline-success rounded d-flex justify-content-center align-items-center ms-2"
              type="submit"
            >
              <img style={{ width: "20px" }} src={searchImage} alt="search" />
            </button>
            <button
              type="button"
              className="btn btn-primary btn-sm text-nowrap ms-auto"
            >
              +Add Post
            </button>
          </div>
        </nav>
      </div>
    </>
  );
}

export default MainWindow;
