const defaultState = {
        currentUser: [
            {
                id: 1,
            }
        ]
}


const storeReducer = (state = defaultState, action) => {
    switch (action.type) {
        case 'SET_POST':
            return {

            }
        case 'SET_TEST':
            return {
                ...state,
                currentUser: action.payload
            }
        default:
            return defaultState    
    }
}


export default storeReducer;
