import React from 'react';
import closeImage from "../../assets/images/close.svg";
import editImage from "../../assets/images/edit.svg";
import likeImage from "../../assets/images/like.png";
import dislikeImage from "../../assets/images/dislike.png";
import { useDispatch } from "react-redux";
function Comment(props) {
  const dispatch = useDispatch();
  const timeStampStr = (new Date
    (parseInt(props.commentData.date)).toLocaleString());
    const user = localStorage.getItem("login");
  console.log(timeStampStr);
    return (
        <div className="container">
        <div className="mt-2 container-fluid d-flex">
          <button
            className="ms-auto btn btn-outline-success rounded-pill d-flex justify-content-center align-items-center ms-2"
            type="submit"
          >
            <img style={{ width: "20px" }} src={editImage} alt="edit" />
          </button>
          <button
            className="btn btn-outline-danger rounded-pill d-flex justify-content-center align-items-center ms-2"
            type="submit"
          >
            <img style={{ width: "20px" }} src={closeImage} alt="close" />
          </button>
        </div>
        <div className="container-fluid d-flex">
          <div className="ms-2">
            {props.commentData.text}
          </div>
        </div>

        <div className="m-2 d-flex justify-content-between">
          <div>
            <img style={{ width: "20px" }} src={likeImage} alt="like"
              onClick={() => {
                dispatch(props.addCommentLike(props.postData.id,user))
              }}
            />
            <span className="ms-1 me-1">
                {props.commentData.votesNumber}
            </span>
            <img
              style={{ width: "20px" }}
              src={dislikeImage}
              alt="dislike"
            />
          </div>

          <div>{props.commentData.username}</div>
          <div className='fw-light'>{timeStampStr}</div>
        </div>
      </div>
    );
}

export default Comment;
