import React, { useContext, useRef,useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./addPostModal.css";
import { LoginContext } from "../LoginWindow/LoginContext";
// import { useNavigate } from "react-router-dom";
import { useDispatch } from 'react-redux';
import { createPost } from "../../Redux/storeReducer";
import { useNavigate } from 'react-router-dom'

export default function AddPostModal(props) {
  // eslint-disable-next-line no-unused-vars
  const [context, setContext] = useContext(LoginContext);
  const [error, setError] = useState('')
  const textRef = useRef(null);
  const navigate = useNavigate()

  function handleSubmit(e){
    e.preventDefault();

    setError('') // clear error
    const addPost = textRef.current.value; //getting text from input
    if(addPost.length <= 6){
        setError('Text should be more than 6 letters')
        return
    }
    props.setShowAddPostModal(false);
}

  const dispatch = useDispatch();
  return (
    <>
      <div className="blur sign-form-overlay"></div>
      <div id="sign-form" className="sign-form p-3 text-center">
        <div className="d-flex  text-center  justify-content-center align-items-center">
          <h2 className="w-100 text-center ">Add Post</h2>
          <div className="">
            <button
              className="btnClose"
              onClick={() => {
                props.setShowAddPostModal(false);
              }}
            >
              X
            </button>
          </div>
        </div>
        <form className="p-1">
        asd
          <div className="mb-3">
            
            <label htmlFor="exampleInputEmail1" className="form-label">
              Title
            </label>
            <textarea
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="2"
              ref={textRef}
            ></textarea>
          </div>
          <div className="mb-3">
            {!context && <p>Guest</p>}
            {context && <p>You logged as: <span className="text-success">{context}</span></p>}
          </div>
          <div
            className="btn-group"
            role="group"
            aria-label="Basic mixed styles example"
          >
            <button type="button" className="btn btn-primary"
            onClick={() => {
              dispatch(createPost(textRef.current.value, context));
              handleSubmit();
              console.log(textRef.current.value, context);
            }}
            >
              Add
            </button>
            <button type="button" className="btn btn-warning">
              Edit
            </button>
          </div>
        </form>
      </div>
    </>
  );
}
