import React, { useState } from "react";
import EditPostModal from "../EditPostModal/EditPostModal";
import SearchingBar from "../SearchingBar/SearchingBar";
import Header from "./../Header/Header";
import Post from "../Post/Post";
import {useSelector} from 'react-redux'
import AddPostModal from './../AddPostModal/AddPostModal';

function MainWindow() {
  const [showEditPostModal, setShowEditPostModal] = useState(false);
  const [showAddPostModal, setShowAddPostModal] = useState(true);
  const cardData = useSelector(state => state.cardData)

  return (
    <>
      {showEditPostModal && (
        <EditPostModal setShowEditPostModal={setShowEditPostModal} />
      )}
      {showEditPostModal && (
        <AddPostModal setShowAddPostModal={setShowAddPostModal} />
      )}
      <Header />
      <div className="container mt-5 rounded-3 p-0">
        <SearchingBar />
        <section className="border border-primary rounded">
          {cardData.map((item, index) => <Post postData={item} key={index} />)}
          {/* <Post /> */}
        </section>
      </div>
    </>
  );
}

export default MainWindow;
