import {postAPI} from "../api/api";


const SET_TEST = 'SET_TEST'
const SET_DATA = 'SET_DATA'
const SET_POSTS_BY_PAGES = 'SET_POSTS_BY_PAGES';

const defaultState = {
    currentUser: 'Guest',
    searchStr: '',
    pagination: {
        pageSize: 5,
        totalUsersCount: 0,
        currentPage: 1
    },
    cardData: [
        {
            id: 1,
            title: 'MyDream',
            username: 'Jeremy',
            likes: [],
            dislikes: [],
            timeStamp: new Date(),
            image: 'empty',
            isOpen:false,
            comments: [
                {
                    text: 'Spasibo',
                    postId: 1,
                    id: 1,
                    username: 'Aboba',
                    dislikes: [],
                    likes: [],
                    timeStamp: new Date()
                }
            ]
        }
            
    ]

}


const storeReducer = (state = defaultState, action) => {
    switch (action.type) {
        case SET_DATA:
            return {

            }
        case SET_TEST:
            return {...state, currentUser: action.payload}

        case SET_POSTS_BY_PAGES:
            return {...state, cardData: action.payload}

        default:
            return defaultState
    }
}


export const setPostsByPages = (payload) => ({type: SET_POSTS_BY_PAGES, payload})


export const getPostsByPages = (page) => {
    return (dispatch) =>{
        let cardDataBuffer;
        postAPI.getPostsByPages(page).then(
            data => {
                console.log(data)
                cardDataBuffer = [...data.result, ...cardDataBuffer]
                cardDataBuffer = cardDataBuffer.map(item => {
                    item['timeStamp'] = item['date']
                    delete item['date'];
                    return item;
                })
                dispatch(setPostsByPages(cardDataBuffer))
            }
        );

    }}




export default storeReducer;
