import React, {useContext} from 'react';
import { LoginContext } from '../LoginWindow/LoginContext';
import { useNavigate } from 'react-router-dom';
import searchImage from '../assets/images/search.png'
function MainWindow() {
    const [context, setContext] = useContext(LoginContext);
    const navigate = useNavigate();

    function handleLogout() {
        setContext('');
        navigate('/');
    }

    return (
        <>
        {/* border border-primary */}
      <nav className="navbar navbar-dark bg-dark container ">
        <div className="container-fluid">
          <a className="navbar-brand">Olim Tech</a>
          <form className="d-flex  justify-content-center align-items-center">
          <div className={context && 'user-name-top'}>
              {context}</div>
            <button className="btn btn-outline-success" type="submit" 
            onClick={handleLogout}>Logout</button>
          </form>
        </div>
      </nav>
      <div className="container mt-5 border border-primary p-0"> 
        <nav class="navbar navbar-expand-lg bg-light ">
          <div class="container-fluid">
          <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button class="btn btn-outline-success d-flex  justify-content-center align-items-center" type="submit">
           <img style={{width: '20px'}} src={searchImage} alt="search"/>
        </button>
      </form>
          </div>
        </nav>
      </div>
      </>
    );
}

export default MainWindow;
